mapper.insert:

  SELECT IFNULL((SELECT max(id) FROM reader) + 1, 1) as id

insert into reader (id, email, loginName,
  password, reader_name, isValid, salt, age, gender, phone
  )
values (#{id,jdbcType=INTEGER}, #{email,jdbcType=VARCHAR}, #{loginName,jdbcType=VARCHAR},
  #{password,jdbcType=VARCHAR}, #{readerName,jdbcType=VARCHAR}, #{isvalid,jdbcType=INTEGER}, #{salt,jdbcType=VARCHAR},
#{age,jdbcType=INTEGER}, #{gender,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}
  )

mapper.update:
update reader set loginName = #{loginName,jdbcType=VARCHAR},
                  reader_name = #{readerName,jdbcType=VARCHAR},
                  age = #{age,jdbcType=INTEGER},
                  gender = #{gender,jdbcType=VARCHAR},
                  phone = #{phone,jdbcType=VARCHAR}
where id = #{id}
